/*jshint laxcomma:true*/$("#selectLevel1").change(function(e){var t=JSON.parse(document.getElementById("programsJson").innerHTML),n=t[$(this).val()],r=['<option value="">Select a program</option>'];if(n){$("#selectLevel3").hide();$.each(n,function(e,t){if(Object.prototype.toString.call(t)!=="[object Object]")r.push('<option value="'+t+'">'+t+"</option>");else{r[0]='<option value="">Select a topic</option>';for(var n in t)r.push('<option data-i="'+e+'" value="'+n+'">'+n+"</option>")}});$("#selectLevel2").html(r.join("")).show()}else $("#selectLevel2, #selectLevel3").hide()});$("#selectLevel2").change(function(e){var t=JSON.parse(document.getElementById("programsJson").innerHTML),n=$(this),r=$("#selectLevel1").val(),i=n.find(":selected").data("i"),s=n.val(),o=['<option value="">Select a program</option>'];if(i>=0){var u=t[r][i][s];for(var a=0;a<u.length;a++)o.push('<option value="'+u[a]+'">'+u[a]+"</option>");$("#selectLevel3").html(o.join("")).show()}else $("#selectLevel3").hide()});